export default function Main({ game, setGame }) {
    const [cardsList, setCardsList] = useState(shuffle(list));

    return (
        <>
            <main>
                {game.gameStatus === "playing" && (
                    cardsList.map(card => {
                        return (
                            <Card key={card.id}
                                card={card}
                                cardsList={cardsList}
                                setCardsList={setCardsList}
                                game={game}
                                setGame={setGame}
                            />
                        );
                    })
                )}
                {game.gameStatus !== "playing" && (
                    <GameStatus game={game}
                        setGame={setGame}
                        cardsList={cardsList}
                        setCardsList={setCardsList}
                    />
                )}
            </main>
        </>
    );
}